import React from 'react';
import './login.css';
import axios from 'axios';


class login extends React.Component{
    state={

     }
    changeHandler=(event)=>{
        const value = event.target.value;
        this.setState({[event.target.name]: value});
        document.getElementById("Result").innerHTML=" ";
      }
    clickHandler = ()=>{
        axios({
        method: 'post',
        url: 'https://dev-bepsy-api.objectedge.com/oe_commerce_api/occ/v1/oauth/login',
        data: {
            "username":this.state.Name,//"trupti.kashid@objectedge.com",
            "password":this.state.Password//"Objectedge$10", 
        },
        config: { headers: {
                "Authorization":"Bearer YWRtaW46YWRtaW4=",
                'Content-Type': 'application/json'
                }
        }
        })
        .then(function (response) {
            if(response.status === 200){
            document.getElementById("Result").innerHTML="success";
        }
        })
        .catch(function (response) {
            document.getElementById("Result").innerHTML="Failed";
        })
    }
    render(){
    return (
            <div className='container'>
                <h3 className="alignLeft">LOG IN</h3>
                <div className="labelWrapper alignLeft"><label>Name :</label></div>
                <input  
                    type="text"
                    name="Name"
                    className="inputField"
                    onChange={this.changeHandler}
                ></input>
                <br></br>
                <div className="labelWrapper alignLeft"><label>Password :</label></div>
                <input
                    type="password"
                    name="Password"
                    className="inputField"
                    onChange={this.changeHandler}
                ></input>
                <br></br>
                <div className="alignLeft"><button onClick ={this.clickHandler}>Submit</button></div>
                <div id="Result"></div>
            </div>
        );
    }

}

export default login;
