import React from 'react';
import './login.css';
import axios from 'axios';
import Toast from '../toast/toast';
import ReactDOM from 'react-dom';

let Name;
let Password;
let toastValue;
let visibleToast;
const changeHandler=(event)=>{
    const value = event.target.value;
    if(event.target.name== "Name" ){
        Name=value;
    }
    else{
        Password=value;
    }
    visibleToast = "invisible";
    document.getElementById("Result").innerHTML=" ";
  }
const clickHandler = ()=>{
    axios({
    method: 'post',
    url: 'https://dev-bepsy-api.objectedge.com/oe_commerce_api/occ/v1/oauth/login',
    data: {
        "username":Name,//"trupti.kashid@objectedge.com",
        "password":Password//"Objectedge$10", 
    },
    config: { headers: {
            "Authorization":"Bearer YWRtaW46YWRtaW4=",
            'Content-Type': 'application/json'
            }
    }
    })
    .then(function (response) {
        if(response.status === 200){
        setInterval(ReactDOM.render(<Toast  message="success"
        visible= "visible"/>, document.getElementById('toastMsg')),1000);
        document.getElementById("Result").innerHTML="success";
    }
    })
    .catch(function (response) {
        setInterval(ReactDOM.render(<Toast  message="failed"
        visible= "visible"/>, document.getElementById('toastMsg')),1000);
        document.getElementById("Result").innerHTML="Failed";
    })
}
const login= () =>{ 
    return (
        <div>
            <div className='container'>
                <h3 className="alignLeft">LOG IN</h3>
                <div className="labelWrapper alignLeft"><label>Name :</label></div>
                <input  
                    type="text"
                    name="Name"
                    className="inputField"
                    onChange={changeHandler}
                ></input>
                <br></br>
                <div className="labelWrapper alignLeft"><label>Password :</label></div>
                <input
                    type="password"
                    name="Password"
                    className="inputField"
                    onChange={changeHandler}
                ></input>
                <br></br>
                <div className="alignLeft"><button onClick ={clickHandler}>Submit</button></div>
                <div id="Result"></div>
            </div>
            <div id="toastMsg"></div>
        </div>
    );
}
export default login;
