import React from 'react';
import './login.css';
import axios from 'axios';
import Toast from '../toast/toast';
import ReactDOM from 'react-dom';


class Login extends React.Component{ 
    state={
        toastValue:'',
        visibleToast:''
    }
    changeHandler=(event)=>{
        const value = event.target.value;
        this.setState({...this.setState,[event.target.name]:value});
       // this.setState({...this.setState,visibleToast:"invisible"});
       this.renderToast("","invisible");
      }
    clickHandler = ()=>{
        let self = this;
        axios({
        method: 'post',
        url: 'https://dev-bepsy-api.objectedge.com/oe_commerce_api/occ/v1/oauth/login',
        data: {
            "username":this.state.Name,//"trupti.kashid@objectedge.com",
            "password":this.state.Password//"Objectedge$10", 
        },
        config: { headers: {
                "Authorization":"Bearer YWRtaW46YWRtaW4=",
                'Content-Type': 'application/json'
                }
        }
        })
        .then(function (response) {
            if(response.status === 200){
                // this.setState({...this.setState,toastValue:"SUCCESS"});
                // this.setState({...this.setState,visibleToast:"visible"});
                self.renderToast("SUCCESS","visible");
        }
        })
        .catch(function (response) {
            // this.setState({...this.setState,toastValue:"Failed"});
            // this.setState({...this.setState,visibleToast:"visible"});
            self.renderToast("Failed","visible");
        })
    }
    renderToast=(toastValue,visibleToast)=>{
        ReactDOM.render(<Toast  message={toastValue}
            visible= {visibleToast}/>, document.getElementById('toastMsg'));
    }
    closeHandler=()=>{
      //  this.setState({...this.setState,visibleToast:"invisible"});
        this.renderToast("","invisible");
    }
    render(){
        return (
            <div>
                <div className='container'>
                    <h3 className="alignLeft">LOG IN</h3>
                    <div className="labelWrapper alignLeft"><label>Name :</label></div>
                    <input  
                        type="text"
                        name="Name"
                        className="inputField"
                        onChange={this.changeHandler}
                    ></input>
                    <br></br>
                    <div className="labelWrapper alignLeft"><label>Password :</label></div>
                    <input
                        type="password"
                        name="Password"
                        className="inputField"
                        onChange={this.changeHandler}
                    ></input>
                    <br></br>
                    <div className="alignLeft"><button onClick ={this.clickHandler}>Submit</button></div>
                    <div id="Result"></div>
                </div>
                <div id="toastMsg" onClick={this.closeHandler}></div>
            </div>
        );
    }
}
export default Login;
