import React, {Component} from 'react';
import './App.css';
import Login from '../components/login';

class App extends Component  {
  state={
    Authentication:[
      {name:"abc" , password : "abc"}
   ],
    name:'',
    Passwod:'',
    result:''
 }
 nameChangedHandler =(event)=>{
  this.setState({name:event.target.value});
  this.setState({result:""});
 }
 passwordChangedHandler =(event)=>{
  this.setState({Passwod:event.target.value});
  this.setState({result:""});
  }
 clickHandler = ()=>{
     this.state.Authentication.map((details,index)=>{
       if(details.name === this.state.name && details.password === this.state.Passwod)
       {
         this.setState({result:"success"});
         this.setState({name:""});
         this.setState({Passwod:""});
         return 0;
        }
    });
 }
  render(){
    return (
      <div className="App">
        <Login 
        name={this.state.name}
        Password={this.state.Passwod}
        changedName={this.nameChangedHandler}
        changedPasswrd={this.passwordChangedHandler}
        click={this.clickHandler}>
        </Login>
        <div>{this.state.result}</div>
      </div>
    );
  }
}

export default App;
