import React from 'react';
import './login.css';
import axios from 'axios';
import Toast from '../toast/toast';
import ReactDOM from 'react-dom';


class Login extends React.Component{ 
    constructor(){
        super();
        this.url ='https://dev-bepsy-api.objectedge.com/oe_commerce_api/solr/v1/search?query=%7B%22query%22%3A%22bike%22%2C%22offset%22%3A0%2C%22limit%22%3A20%2C%22sort%22%3A%22new+asc%22%2C%22filter%22%3A%5B%22categories%3ArootCategory%2Fb_equipment%2Fb_cycling-accessories%22%2C%22siteId%3AsiteUS%22%2C%22catalog%3Abepsy_catalog_1%22%2C%22dyn_price_defaultPriceGroup%3A%5B0+TO+*%5D%22%2C%22%7B%21collapse+field%3DproductId%7D%22%5D%2C%22facet%22%3A%7B%22categories%22%3A%7B%22type%22%3A%22terms%22%2C%22field%22%3A%22categories%22%2C%22prefix%22%3A%22rootCategory%2Fb_equipment%2Fb_cycling-accessories%22%2C%22limit%22%3A100%7D%2C%22dyn_price_defaultPriceGroup%22%3A%7B%22type%22%3A%22range%22%2C%22field%22%3A%22dyn_price_defaultPriceGroup%22%2C%22domain%22%3A%7B%22excludeTags%22%3A%22PRICE%22%7D%2C%22start%22%3A0%2C%22end%22%3A7000%2C%22gap%22%3A1000%7D%2C%22type%22%3A%7B%22type%22%3A%22terms%22%2C%22field%22%3A%22type%22%2C%22limit%22%3A100%7D%2C%22brand%22%3A%7B%22type%22%3A%22terms%22%2C%22field%22%3A%22brand%22%2C%22limit%22%3A100%7D%7D%7D';
        this.Authorization ="Bearer YWRtaW46YWRtaW4=";
        this.contentType = 'application/json';
    }
    state={
     Name:"",
     Password:''
    }
    changeHandler=(event)=>{
        const value = event.target.value;
        this.setState({...this.setState,[event.target.name]:value});
        this.renderToast("","invisible");
      }
    inputValidation=()=>{
        // if(/^\S*$/.test(this.state.Name) && /^\S*$/.test(this.state.Password)){
           this.clickHandler();
        // }
        // else{
        //     this.renderToast("Invalid Input","visible");
        // }
    }
    clickHandler = ()=>{
        let self = this;
        axios({
        method: 'post',
        url: this.url,
        data: {
            "username":this.state.Name,//"trupti.kashid@objectedge.com",
            "password":this.state.Password//"Objectedge$10", 
        },
        config: { headers: {
                // "Authorization":this.Authorization,
                // 'Content-Type': this.contentType
                "Bepsy-CatalogId": "bepsy_catalog_1",
                "Bepsy-PricelistId": "defaultPriceGroup",
                "Bepsy-SiteId": "siteUS"

                }
        }
        })
        .then(function (response) {
            if(response.status === 200){
                console.log(response);
                self.renderToast("success","visible");
            }
        })
        .catch(function (response) {
            self.renderToast("failed","visible");
        })
    }
    renderToast=(toastValue,visibleToast)=>{
        ReactDOM.render(<Toast  message={toastValue}
            visible= {visibleToast}/>, document.getElementById('toastMsg'));
    }
    closeHandler=()=>{
        this.renderToast("","invisible");
    }
    render(){
        return (
            <div>
                <div className='container'>
                    <h3 className="alignLeft">LOG IN</h3>
                    <div className="labelWrapper alignLeft"><label>Name :</label></div>
                    <input  
                        type="text"
                        name="Name"
                        className="inputField"
                        onChange={this.changeHandler}
                    required></input>
                    <br></br>
                    <div className="labelWrapper alignLeft"><label>Password :</label></div>
                    <input
                        type="password"
                        name="Password"
                        className="inputField"
                        onChange={this.changeHandler}
                    required></input>
                    <br></br>
                    <div className="alignLeft"><button onClick ={this.inputValidation}>Submit</button></div>
                    <div id="Result"></div>
                </div>
                <div id="toastMsg" onClick={this.closeHandler}></div>
            </div>
        );
    }
}
export default Login;
